import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CommunityWriteForm.css';

const CommunityWriteForm = () => {
  const [category, setCategory] = useState('30');
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [invalidSentences, setInvalidSentences] = useState([]);
  const [filtered, setFiltered] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault(); // 새로고침 방지

    const requestBody = {
      title: title.trim(),
      contents: content.trim(),
      category: mapCategoryToBackendFormat(category),
    };

    try {
      const response = await fetch('http://localhost:8080/api/user/post', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      console.log('Response status:', response.status);
      const result = await response.json();
      console.log('Result:', result);

      if (result.filtered) {
        setInvalidSentences(result.invalidSentences);
        setFiltered(true);
        setIsSubmitted(false);
      } else {
        setIsSubmitted(true);
        setFiltered(false);
        setInvalidSentences([]);

        // 작성된 게시글이 포함된 상태로 카테고리 페이지로 이동
        navigate(`/community/gomin/${category}`, { state: { newPost: requestBody } });
      }
    } catch (error) {
      console.error('서버와의 통신 중 오류 발생:', error);
    }
  };

  const mapCategoryToBackendFormat = (category) => ({
    '10': '_10S',
    '20': '_20S',
    '30': '_30S',
    '40': '_40S',
    gomsin: 'MILITARY',
    CC: 'CC',
    office: 'OFFICE',
    'date-places': 'DATE_LOCATION',
    gifts: 'ANNIVERSARY_GIFT',
    travel: 'TRAVEL_LOCATION',
  }[category] || category);

  return (
    <form className="write-form" onSubmit={handleSubmit}>
      <div className="form-header">
        <button className="back-button" onClick={() => window.history.back()}>
          &larr;
        </button>
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="10">10대 고민</option>
          <option value="20">20대 고민</option>
          <option value="30">30대 고민</option>
          <option value="40">40대 고민</option>
          <option value="gomsin">곰신 고민</option>
          <option value="CC">CC 고민</option>
          <option value="office">사내연애 고민</option>
          <option value="date-places">데이트 장소 추천</option>
          <option value="gifts">기념일 선물 추천</option>
          <option value="travel">커플 여행지 추천</option>
        </select>
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="제목" />
      </div>
      <textarea
        className={`content-textarea ${filtered ? 'error-content' : isSubmitted ? 'success-content' : ''}`}
        value={content}
        onChange={(e) => setContent(e.target.value)}
        placeholder="내용"
      ></textarea>

      {filtered && (
        <div className="error-message">
          <p>다음 문장을 수정해주세요:</p>
          {invalidSentences.map((sentence, index) => (
            <p key={index}>{sentence}</p>
          ))}
        </div>
      )}

      {isSubmitted && !filtered && <div className="success-message">게시글 작성이 완료되었습니다!</div>}

      <div className="form-footer">
        <button type="submit">작성하기</button>
      </div>
    </form>
  );
};

export default CommunityWriteForm;
