import React, { useState, useEffect } from 'react';
import './HeroSlider.css';

const HeroSlider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const slides = [
    '/img/homeCategory1.png',
    '/img/homeCategory2.png',
    '/img/homeCategory3.png',
    '/img/homeCategory4.png',
    '/img/homeCategory5.png',
    '/img/homeCategory6.png'
  ];

  // 슬라이드 변경 핸들러 (좌우 화살표)
  const nextSlide = () => {
    setCurrentSlide((prev) => (prev === slides.length - 1 ? 0 : prev + 1));
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev === 0 ? slides.length - 1 : prev - 1));
  };

  // 3초마다 자동으로 슬라이드 넘김
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 3000); // 3초마다 슬라이드
    return () => clearInterval(interval); // 컴포넌트 언마운트 시 인터벌 제거
  }, []);

  return (
    <div className="hero-slider-container">
      {/* 슬라이드 컨테이너 */}
      <div
        className="hero-slider"
        style={{ transform: `translateX(-${currentSlide * 100}%)` }} // 슬라이드를 왼쪽으로 이동
      >
        {slides.map((slide, index) => (
          <div
            key={index}
            className="slide"
          >
            <img src={slide} alt={`배너 ${index + 1}`} />
          </div>
        ))}
      </div>

      {/* 이전/다음 화살표 */}
      <button className="prev-arrow" onClick={prevSlide}>
        &#10094;
      </button>
      <button className="next-arrow" onClick={nextSlide}>
        &#10095;
      </button>
    </div>
  );
};

export default HeroSlider;
