import React, { useState, useEffect, useRef } from 'react';
import './HeroSlider.css';

const HeroSlider = () => {
  const [currentSlide, setCurrentSlide] = useState(1); // 복사된 첫 번째 슬라이드로 시작
  const [isTransitioning, setIsTransitioning] = useState(true); // 트랜지션 활성화 여부
  const slides = [
    '/img/homeCategory1.png',
    '/img/homeCategory2.png',
    '/img/homeCategory3.png',
    '/img/homeCategory4.png',
    '/img/homeCategory5.png',
    '/img/homeCategory6.png'
  ];

  const sliderRef = useRef(null);

  // 슬라이드를 복사해서 앞뒤에 추가
  const totalSlides = [slides[slides.length - 1], ...slides, slides[0]];

  const nextSlide = () => {
    setCurrentSlide((prev) => prev + 1);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => prev - 1);
  };

  // 3초마다 자동으로 슬라이드 넘김
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 3000);
    return () => clearInterval(interval); // 컴포넌트 언마운트 시 인터벌 제거
  }, []);

  // 슬라이드 이동 후 처리 (복사된 슬라이드에서 실제 슬라이드로 이동)
  useEffect(() => {
    if (currentSlide === totalSlides.length - 1) {
      // 마지막 슬라이드 도달 시 첫 번째 실제 슬라이드로 스냅
      setTimeout(() => {
        setIsTransitioning(false); // 트랜지션 비활성화
        setCurrentSlide(1); // 첫 번째 슬라이드로 이동
      }, 500); // 트랜지션이 끝난 후 비활성화
    } else if (currentSlide === 0) {
      // 첫 슬라이드에서 마지막 실제 슬라이드로 스냅
      setTimeout(() => {
        setIsTransitioning(false);
        setCurrentSlide(totalSlides.length - 2); // 마지막 슬라이드로 이동
      }, 500);
    } else {
      setIsTransitioning(true); // 트랜지션 활성화
    }
  }, [currentSlide]);

  return (
    <div className="hero-slider-container">
      <div
        ref={sliderRef}
        className="hero-slider"
        style={{
          transform: `translateX(-${currentSlide * 100}%)`,
          transition: isTransitioning ? 'transform 0.5s ease-in-out' : 'none'
        }}
      >
        {totalSlides.map((slide, index) => (
          <div key={index} className="slide">
            <img src={slide} alt={`배너 ${index + 1}`} />
          </div>
        ))}
      </div>

      {/* 이전/다음 화살표 */}
      <button className="prev-arrow" onClick={prevSlide}>
        &#10094;
      </button>
      <button className="next-arrow" onClick={nextSlide}>
        &#10095;
      </button>
    </div>
  );
};

export default HeroSlider;
