import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import CommunityNavigation from '../components/CommunityNavigation';
import './GominDetailPage.css';

const GominDetailPage = () => {
  const { category } = useParams(); // URL에서 카테고리 파라미터 가져오기
  const [posts, setPosts] = useState([]); // 게시글 목록 상태
  const [hotPost, setHotPost] = useState(null); // 핫 게시글 상태
  const [pagination, setPagination] = useState({ totalPages: 5, currentPage: 0 });

  const navigate = useNavigate(); // 페이지 이동을 위한 useNavigate 사용

  // 게시글 클릭 시 상세 페이지로 이동
  const handlePostClick = (postId) => {
    navigate(`/post/${postId}`); // 게시글 상세 페이지로 이동
  };

  // 작성하기 버튼 클릭 시 페이지 이동
  const handleWritePost = () => {
    navigate('/community/write'); // 게시글 작성 페이지로 이동
  };

  // API를 통해 게시글 목록을 가져오는 함수
  const fetchPosts = async () => {
    try {
      const response = await fetch(`http://localhost:8080/api/common/post?category=${category}`);
      if (!response.ok) {
        throw new Error('게시글을 불러오지 못했습니다.');
      }
      const data = await response.json();
      const { hotPost, posts } = data.contents[0];

      setPosts(posts);
      setHotPost(hotPost);
      setPagination({ totalPages: data.totalPages, currentPage: data.currentPage });
    } catch (error) {
      console.error('API 호출 중 오류 발생:', error);
    }
  };

  // 페이지 로드 시 게시글을 가져옴
  useEffect(() => {
    fetchPosts();
  }, [category]);

  return (
    <div className="gomin-detail-page">
      <CommunityNavigation /> {/* 네비게이션 추가 */}

      <div className="gomin-content">
        <h2>고민 / {category} 대</h2>

        {/* 게시글 목록 테이블 */}
        <table className="post-list-table">
          <thead>
            <tr>
              <th>제목</th>
              <th>작성일시</th>
              <th>조회수</th>
              <th>좋아요</th>
            </tr>
          </thead>
          <tbody>
            {hotPost && (
              <tr className="hot-post" onClick={() => handlePostClick(hotPost.id)}>
                <td>{hotPost.title}</td>
                <td>{hotPost.createdAt}</td>
                <td>{hotPost.viewCount}</td>
                <td>{hotPost.likeCount}</td>
              </tr>
            )}
            {posts.length === 0 ? (
              <tr>
                <td colSpan="4">게시글이 없습니다.</td>
              </tr>
            ) : (
              posts.map((post) => (
                <tr key={post.id} onClick={() => handlePostClick(post.id)}>
                  <td>{post.title}</td>
                  <td>{post.createdAt}</td>
                  <td>{post.viewCount}</td>
                  <td>{post.likeCount}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>

        {/* 작성하기 버튼 */}
        <div className="write-post-container">
          <button className="write-post-button" onClick={handleWritePost}>
            작성하기
          </button>
        </div>
      </div>
    </div>
  );
};

export default GominDetailPage;
