import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import './GominDetailPage.css';

const GominDetailPage = () => {
  const { category } = useParams();
  const { state } = useLocation(); // 작성된 게시글 상태 받아오기
  const [posts, setPosts] = useState([]);
  const [hotPost, setHotPost] = useState(null);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch(
          `http://localhost:8080/api/common/post?category=${category}&page=0&size=10`
        );
        const data = await response.json();
        const postsFromApi = data.contents[0]?.posts || [];

        // 새로 작성된 게시글이 있을 경우 목록에 추가
        if (state?.newPost) {
          setPosts([state.newPost, ...postsFromApi]);
        } else {
          setPosts(postsFromApi);
        }
        setHotPost(data.contents[0]?.hotPost || null);
      } catch (error) {
        console.error('게시글을 불러오는 중 오류 발생:', error);
      }
    };

    fetchPosts();
  }, [category, state]);

  return (
    <div className="gomin-detail-page">
      <h2>{category} 게시판</h2>
      {hotPost && (
        <div className="hot-post">
          <h3>{hotPost.title}</h3>
        </div>
      )}
      <ul>
        {posts.map((post) => (
          <li key={post.id}>{post.title}</li>
        ))}
      </ul>
    </div>
  );
};

export default GominDetailPage;
