import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './CommunityNavigation.css'; // 스타일 파일

const CommunityNavigation = () => {
  const location = useLocation(); // 현재 경로 가져오기
  const [isGominOpen, setIsGominOpen] = useState(false);
  const [isInfoOpen, setIsInfoOpen] = useState(false);
  const [activeCategory, setActiveCategory] = useState(null); // 클릭된 세부 카테고리 상태 관리

  // 현재 경로에 따라 카테고리 상태 설정
  useEffect(() => {
    if (location.pathname.includes('/community/gomin')) {
      setIsGominOpen(true);
      setIsInfoOpen(false); // 다른 카테고리는 닫기
    } else if (location.pathname.includes('/community/info')) {
      setIsInfoOpen(true);
      setIsGominOpen(false); // 다른 카테고리는 닫기
    } else {
      setIsGominOpen(false);
      setIsInfoOpen(false);
    }

    // 활성화된 세부 카테고리 설정
    const activePath = location.pathname.split('/').pop(); // 마지막 경로 추출
    setActiveCategory(activePath);
  }, [location]);

  return (
    <div className="community-navigation">
      {/* 고민 카테고리 */}
      <div className="category">
        <div className="category-header" onClick={() => setIsGominOpen(!isGominOpen)}>
          고민
          <span className={isGominOpen ? 'arrow-up' : 'arrow-down'}></span>
        </div>
        {isGominOpen && (
          <ul className="category-list">
            {['10', '20', '30', '40', 'gomsin', 'cc', 'office'].map((age) => (
              <li key={age}>
                <Link
                  to={`/community/gomin/${age}`}
                  className={activeCategory === age ? 'active' : ''}
                  onClick={() => setActiveCategory(age)}
                >
                  • {age === '10' ? '10대 고민' : `${age}대 고민`}
                </Link>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* 정보 카테고리 */}
      <div className="category">
        <div className="category-header" onClick={() => setIsInfoOpen(!isInfoOpen)}>
          정보
          <span className={isInfoOpen ? 'arrow-up' : 'arrow-down'}></span>
        </div>
        {isInfoOpen && (
          <ul className="category-list">
            {['date-places', 'gifts', 'travel'].map((info) => (
              <li key={info}>
                <Link
                  to={`/community/info/${info}`}
                  className={activeCategory === info ? 'active' : ''}
                  onClick={() => setActiveCategory(info)}
                >
                  • {info === 'date-places'
                    ? '데이트 장소 추천'
                    : info === 'gifts'
                    ? '기념일 선물 추천'
                    : '커플 여행지 추천'}
                </Link>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default CommunityNavigation;
