// src/pages/CommunityWriteForm.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CommunityWriteForm.css';

const CommunityWriteForm = () => {
  const [category, setCategory] = useState('30');
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const navigate = useNavigate();  // 페이지 이동 훅

  const handleSubmit = async (e) => {
    e.preventDefault(); // 새로고침 방지

    const requestBody = {
      title,
      contents: content,
      category: mapCategoryToBackendFormat(category),
    };

    try {
      const response = await fetch('http://localhost:8080/api/user/post', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      const result = await response.json();
      if (!result.filtered) {
        navigate(`/community/gomin/${category}`, {
          state: { newPost: { title, content, id: Date.now(), category } }
        });
      }
    } catch (error) {
      console.error('게시글 작성 중 오류 발생:', error);
    }
  };

  const mapCategoryToBackendFormat = (category) => {
    const categoryMap = {
      '10': '_10S',
      '20': '_20S',
      '30': '_30S',
      '40': '_40S',
      'gomsin': 'MILITARY',
      'CC': 'CC',
      'office': 'OFFICE',
      'date-places': 'DATE_LOCATION',
      'gifts': 'ANNIVERSARY_GIFT',
      'travel': 'TRAVEL_LOCATION'
    };
    return categoryMap[category] || category;
  };

  return (
    <form className="write-form" onSubmit={handleSubmit}>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="제목"
      />
      <textarea
        value={content}
        onChange={(e) => setContent(e.target.value)}
        placeholder="내용"
      />
      <button type="submit">작성하기</button>
    </form>
  );
};

export default CommunityWriteForm;
