import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import CommunityNavigation from '../components/CommunityNavigation';
import './GominDetailPage.css';

const GominDetailPage = () => {
  const { category } = useParams(); // URL에서 카테고리 파라미터 가져오기
  const [posts, setPosts] = useState([]); // 게시글 데이터 상태
  const [hotPost, setHotPost] = useState(null); // 핫 게시글 상태
  const navigate = useNavigate(); // 페이지 이동을 위한 navigate

  // API를 통해 게시글 데이터 가져오기
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch(
          `http://localhost:8080/api/common/post?category=${category}`
        );
        const data = await response.json();
        const { contents } = data.contents[0]; // 응답에서 데이터 추출

        setPosts(contents.posts); // 일반 게시글 설정
        setHotPost(contents.hotPost); // 핫 게시글 설정
      } catch (error) {
        console.error('게시글을 가져오는 중 오류 발생:', error);
      }
    };

    fetchPosts(); // 페이지 로드 시 API 호출
  }, [category]);

  return (
    <div className="gomin-detail-page">
      <CommunityNavigation />
      <h2>고민 / {category}</h2>

      <table className="post-list-table">
        <thead>
          <tr>
            <th>제목</th>
            <th>작성일시</th>
            <th>조회수</th>
            <th>좋아요</th>
          </tr>
        </thead>
        <tbody>
          {hotPost && (
            <tr onClick={() => navigate(`/post/${hotPost.id}`)}>
              <td>{hotPost.title}</td>
              <td>{hotPost.createdAt}</td>
              <td>{hotPost.viewCount}</td>
              <td>{hotPost.likesCount}</td>
            </tr>
          )}
          {posts.length === 0 ? (
            <tr>
              <td colSpan="4">게시글이 없습니다.</td>
            </tr>
          ) : (
            posts.map((post) => (
              <tr key={post.id} onClick={() => navigate(`/post/${post.id}`)}>
                <td>{post.title}</td>
                <td>{post.createdAt}</td>
                <td>{post.viewCount}</td>
                <td>{post.likesCount}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default GominDetailPage;
